import React from 'react';
import data from './data';
import './App.css';

class App extends React.Component {
  state = {
    players: data,
    hasBeenClicked: []
  }
  renderPlayers = () => {
    return this.state.players.map(player => <img src={player.url} onClick={this.handleClick} value={player.name}/>)
  }
  handleClick = e => {
    let player = e.currentTarget.getAttribute("value");
    let newArr = this.state.hasBeenClicked.concat(player);
    this.setState({
      hasBeenClicked: newArr
    })
    console.log(this.state.hasBeenClicked);
  }
  render() {
    return (
      <div className="App">
        <h1>Clicky Game</h1>
        {this.renderPlayers()}
      </div>
    );
  }
}

export default App;
